(5732, 498, 'content_potential_0_headline', 'Tập trung vào thị trường'),
(5733, 498, '_content_potential_0_headline', 'field_61371117e6b4e'),
(5734, 498, 'content_potential_0_headline_content', 'Thị trường của chúng tôi chủ yếu là châu Âu, Mỹ, Úc... Chúng tôi hiểu văn hóa địa phương, chính sách, yêu cầu sản phẩm...'),
(5735, 498, '_content_potential_0_headline_content', 'field_6137114ce6b4f'),
(5736, 498, 'content_potential_0_image_potential', '402'),
(5737, 498, '_content_potential_0_image_potential', 'field_61371163e6b50'),
(5738, 498, 'content_potential_1_headline', 'Nguyên liệu chất lượng'),
(5739, 498, '_content_potential_1_headline', 'field_61371117e6b4e'),
(5740, 498, 'content_potential_1_headline_content', 'Chúng tôi có đầy đủ các nguyên liệu chất lượng cao để sản xuất tất cả các loại túi nhựa từ polyethylene (HDPE, LDPE, LLDPE) như túi phân hủy sinh học, túi nhựa phân hủy (EPI, D2W, P D) để bảo vệ môi trường của chúng ta.'),
(5741, 498, '_content_potential_1_headline_content', 'field_6137114ce6b4f'),
(5742, 498, 'content_potential_1_image_potential', '403'),
(5743, 498, '_content_potential_1_image_potential', 'field_61371163e6b50'),
(5744, 498, 'content_potential_2_headline', 'Máy móc công nghệ cao được trang bị'),
(5745, 498, '_content_potential_2_headline', 'field_61371117e6b4e'),
(5746, 498, 'content_potential_2_headline_content', 'Nhựa VN sở hữu nhiều máy móc công nghệ cao để sản xuất túi như áo thun, Cuộn. Nhiều máy có thể tự động thay lõi, vận hành 2-3 dây chuyền cùng một lúc cho tốc độ sản xuất vượt trội. Đặc biệt, chúng tôi sở hữu máy sản xuất áo phông Hemingstone. Đây là một thương hiệu rất nổi tiếng và phổ biến hiện nay.'),
(5747, 498, '_content_potential_2_headline_content', 'field_6137114ce6b4f'),
